import "./App.css";
import React, { useState, useEffect, useRef } from "react";

function App() {
  const [scrollCor, setScrollCor] = useState({ X: 0, Y: 0 });
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const elementRef = useRef(null);

  const scrollHandler = (e) => {
    let x = window.scrollX;
    let y = window.scrollY;
    setScrollCor({ x, y });
    console.warn(scrollCor.X, scrollCor.Y);
  };

  const checkval = () => {
    if (scrollCor.Y >= position.y && scrollCor.X >= position.X) {
      let para = document.getElementById("para");
      para.style.opacity = 1;
    } else {
      let para = document.getElementById("para");
      para.style.opacity = 0;
    }
  };

  useEffect(() => {
    function handleResize() {
      const x = elementRef.current.offsetLeft;
      const y = elementRef.current.offsetTop;
      setPosition({ x, y });
    }

    handleResize();
    window.addEventListener("scroll", handleResize);
    // return () => window.removeEventListener("resize", handleResize);
  }, [elementRef]);

  useEffect(() => {
    window.addEventListener("scroll", scrollHandler);
    console.warn(scrollCor.X, scrollCor.Y);
    console.warn(position.x, position.y);
    checkval();
  });

  return (
    <section className=" bg-black font-customised1">
      <div className=" w-[100%] mx-auto flex items-center justify-center">
        <div className=" relative h-[200vh]">
          <div className=" sticky top-0 left-0 py-20 flex items-center h-[50%]">
            <div className=" max-w-5xl">
              <div className=" relative">
                <p className=" text-white text-4xl font-semibold leading-none absolute flex flex-wrap px-5 gap-2">
                  <span>In </span>
                  <span>Chronicle </span>
                  <span>everything </span>
                  <span>is </span>
                  <span>made </span>
                  <span>with </span>
                  <span>Blocks </span>
                  <span>that </span>
                  <span>come </span>
                  <span>with </span>
                  <span>pixel </span>
                  <span>perfect </span>
                  <span>design, </span>
                  <span>interactivity </span>
                  <span>and </span>
                  <span>motion </span>
                  <span>out </span>
                  <span>of </span>
                  <span>the </span>
                  <span>box. </span>
                  <span>Instead </span>
                  <span>of </span>
                  <span>designing </span>
                  <span>from </span>
                  <span>scratch, </span>
                  <span>simply </span>
                  <span>choose </span>
                  <span>the </span>
                  <span>right </span>
                  <span>one </span>
                  <span>from </span>
                  <span>our </span>
                  <span>library </span>
                  <span>of </span>
                  <span>blocks </span>
                  <span>and </span>
                  <span>see </span>
                  <span
                    ref={elementRef}
                    id="para"
                    className=" opacity-0 transition-opacity duration-300"
                  >
                    the
                  </span>
                  <span>magic </span>
                  <span>unfold</span>
                </p>
                <p className=" text-[#202020] text-4xl font-semibold leading-none flex flex-wrap px-5 gap-2">
                  <span>In </span>
                  <span>Chronicle </span>
                  <span>everything </span>
                  <span>is </span>
                  <span>made </span>
                  <span>with </span>
                  <span>Blocks </span>
                  <span>that </span>
                  <span>come </span>
                  <span>with </span>
                  <span>pixel </span>
                  <span>perfect </span>
                  <span>design, </span>
                  <span>interactivity </span>
                  <span>and </span>
                  <span>motion </span>
                  <span>out </span>
                  <span>of </span>
                  <span>the </span>
                  <span>box. </span>
                  <span>Instead </span>
                  <span>of </span>
                  <span>designing </span>
                  <span>from </span>
                  <span>scratch, </span>
                  <span>simply </span>
                  <span>choose </span>
                  <span>the </span>
                  <span>right </span>
                  <span>one </span>
                  <span>from </span>
                  <span>our </span>
                  <span>library </span>
                  <span>of </span>
                  <span>blocks </span>
                  <span>and </span>
                  <span>see </span>
                  <span>the </span>
                  <span>magic </span>
                  <span>unfold </span>
                </p>
              </div>
            </div>
          </div>
          <div className=" absolute top-[33%] left-0 h-[16.666%] w-[100%]"></div>
        </div>
      </div>
    </section>
  );
}

export default App;
