import React, { useState, useEffect, useRef } from "react";

const fadeSlider = () => {
  const [scrollCor, setScrollCor] = useState(0);

  const scrollHandler = (e) => {
    let y = window.scrollY;
    setScrollCor(y);
    console.warn(scrollCor);
  };

  const addAnimaton = () => {
    let height = window.screen.height;
    let slide1 = document.getElementById("slide1");
    let slide2 = document.getElementById("slide2");
    let slide3 = document.getElementById("slide3");

    if (scrollCor > 0 && scrollCor < 2 * height) {
      slide1.style.opacity = 1;
      slide1.style.transform = "translate(0px,0px)";
      slide1.style.transform = "scale(1,1)";

      slide2.style.opacity = 0;
      slide2.style.transform = "translate(0px,250px)";
      slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 0;
      slide3.style.transform = "translate(0px,250px)";
      slide3.style.transform = "scale(0.8,0.8)";
    } else if (scrollCor >= 2 * height && scrollCor < 4 * height) {
      slide1.style.opacity = 0;
      slide1.style.transform = "translate(0px,250px)";
      slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 1;
      slide2.style.transform = "translate(0px,0px)";
      slide2.style.transform = "scale(1,1)";

      slide3.style.opacity = 0;
      slide3.style.transform = "translate(0px,250px)";
      slide3.style.transform = "scale(0.8,0.8)";
    } else if (scrollCor >= 4 * height) {
      slide1.style.opacity = 0;
      slide1.style.transform = "translate(0px,250px)";
      slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 0;
      slide2.style.transform = "translate(0px,250px)";
      slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 1;
      slide3.style.transform = "translate(0px,0px)";
      slide3.style.transform = "scale(1,1)";
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", scrollHandler);
    console.warn(scrollCor);
    addAnimaton();
  });
  return (
    <div className=" relative h-[600vh] w-1/2">
      <div className=" sticky top-0 left-0 h-screen w-full flex items-center bg-red-500">
        <div
          className=" absolute top-0 left-0 h-screen w-full p-16 opacity-1 translate-y-[0px] bg-green-500 transition-all duration-700 delay-200 rounded-lg"
          rounded-lg
          id="slide1"
        >
          {" "}
          uhybcjnxewicx
        </div>
        <div
          className=" absolute top-0 left-0 h-screen w-full p-16 opacity-0 translate-y-[250px] bg-pink-500 transition-all duration-700 delay-200 rounded-lg"
          id="slide2"
        >
          {" "}
          hjbcnjdxknms
        </div>
        <div
          className=" absolute top-0 left-0 h-screen w-full p-16 opacity-0 translate-y-[250px] bg-blue-500 transition-all duration-700 delay-200 rounded-lg"
          id="slide3"
        >
          {" "}
          njhxnwjkxkxm
        </div>
      </div>
    </div>
  );
};

export default fadeSlider;
