import React from "react";
import img from "../images/img.jpg";
import img2 from "../images/img2.jpg";

const NewFadeSlider = () => {
  const [Trans, setTrans] = React.useState(0);

  const nextSlide = () => {
    let slide1 = document.getElementById("slide1");
    let slide2 = document.getElementById("slide2");
    let slide3 = document.getElementById("slide3");
    if (Trans === 3) {
      setTrans(1);
    } else {
      setTrans(Trans + 1);
    }
    if (Trans === 1) {
      slide1.style.opacity = 1;
      //   slide1.style.transform = "translate(0px,0px)";
      //   slide1.style.transform = "scale(1,1)";

      slide2.style.opacity = 0;
      //   slide2.style.transform = "translate(-250px,0px)";
      //   slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 0;
      //   slide3.style.transform = "translate(-250px,0px)";
      //   slide3.style.transform = "scale(0.8,0.8)";
    } else if (Trans === 2) {
      slide1.style.opacity = 0;
      //   slide1.style.transform = "translate(-250px,0px)";
      //   slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 1;
      //   slide2.style.transform = "translate(0px,0px)";
      //   slide2.style.transform = "scale(1,1)";

      slide3.style.opacity = 0;
      //   slide3.style.transform = "translate(-250px,0px)";
      //   slide3.style.transform = "scale(0.8,0.8)";
    } else {
      slide1.style.opacity = 0;
      //   slide1.style.transform = "translate(-250px,0px)";
      //   slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 0;
      //   slide2.style.transform = "translate(-250px,0px)";
      //   slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 1;
      //   slide3.style.transform = "translate(0px,0px)";
      //   slide3.style.transform = "scale(1,1)";
    }
  };

  const prevSlide = () => {
    let slide1 = document.getElementById("slide1");
    let slide2 = document.getElementById("slide2");
    let slide3 = document.getElementById("slide3");
    if (Trans === 3) {
      setTrans(1);
    } else {
      setTrans(Trans + 1);
    }
    if (Trans === 1) {
      slide1.style.opacity = 1;
      //   slide1.style.transform = "translate(0px,0px)";
      //   slide1.style.transform = "scale(1,1)";

      slide2.style.opacity = 0;
      //   slide2.style.transform = "translate(-250px,0px)";
      //   slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 0;
      //   slide3.style.transform = "translate(-250px,0px)";
      //   slide3.style.transform = "scale(0.8,0.8)";
    } else if (Trans === 2) {
      slide1.style.opacity = 0;
      //   slide1.style.transform = "translate(-250px,0px)";
      //   slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 1;
      //   slide2.style.transform = "translate(0px,0px)";
      //   slide2.style.transform = "scale(1,1)";

      slide3.style.opacity = 0;
      //   slide3.style.transform = "translate(-250px,0px)";
      //   slide3.style.transform = "scale(0.8,0.8)";
    } else {
      slide1.style.opacity = 0;
      //   slide1.style.transform = "translate(-250px,0px)";
      //   slide1.style.transform = "scale(0.8,0.8)";

      slide2.style.opacity = 0;
      //   slide2.style.transform = "translate(-250px,0px)";
      //   slide2.style.transform = "scale(0.8,0.8)";

      slide3.style.opacity = 1;
      //   slide3.style.transform = "translate(0px,0px)";
      //   slide3.style.transform = "scale(1,1)";
    }
  };
  return (
    <div className=" w-full h-[100vh] relative bg-black">
      <div
        className=" opacity-1 absolute w-[75%] top-[50%] -translate-y-[50%] left-[50%] -translate-x-[50%]  h-screen mx-auto py-16 lg:px-10  transition-all duration-700 delay-200 rounded-lg"
        id="slide1"
      >
        <div className=" w-[100%] h-[400px] bg-gray-700 rounded-lg flex flex-col justify-center items-center my-14 overflow-hidden">
          <div className=" w-[100%] ">
            <img className=" w-[100%] h-[200px]" src={img} />
          </div>
          <div className=" w-[100%] h-[50%]">
            <h1 className=" text-white text-center text-2xl">
              A keyboard <br /> first experience
            </h1>
            <br />
            <p className=" text-center text-white pb-4 mx-2">
              Powerful shortcuts and a keyboard-first workflow means you will
              get to your finish line in no time!
            </p>
          </div>
        </div>
      </div>
      <div
        className=" opacity-0 absolute w-[75%] mx-auto top-[50%] -translate-y-[50%] left-[50%] -translate-x-[50%] h-screen py-16 lg:px-10 transition-all duration-700 delay-200 rounded-lg"
        id="slide2"
      >
        <div className=" w-[100%]  h-[400px] bg-gray-700 rounded-lg flex flex-col justify-center items-center my-14 overflow-hidden">
          <div className=" w-[100%]">
            <img className=" w-[100%] h-[200px]" src={img2} />
          </div>
          <div className=" w-[100%] h-[50%]">
            <h1 className=" text-white text-center text-2xl">
              A keyboard <br /> first experience
            </h1>
            <br />
            <p className=" text-center text-white pb-4 mx-2">
              Powerful shortcuts and a keyboard-first workflow means you will
              get to your finish line in no time!
            </p>
          </div>
        </div>
      </div>
      <div
        className=" opacity-0 absolute w-[75%] mx-auto top-[50%] -translate-y-[50%] left-[50%] -translate-x-[50%] h-screen py-16 lg:px-10 transition-all duration-700 delay-200 rounded-lg"
        id="slide3"
      >
        <div className=" w-[100%] h-[400px] bg-gray-700 rounded-lg flex flex-col justify-center items-center my-14 overflow-hidden">
          <div className="w-[100%]">
            <img className=" w-[100%] h-[200px]" src={img2} />
          </div>
          <div className=" w-[100%] h-[50%] flex items-center flex-col">
            <h1 className=" text-white text-center text-2xl">
              A keyboard <br /> first experience
            </h1>
            <br />
            <p className=" text-center text-white pb-4 mx-2">
              Powerful shortcuts and a keyboard-first workflow means you will
              get to your finish line in no time!
            </p>
          </div>
        </div>
      </div>
      <div className=" absolute top-[75%] -translate-y-[75%] left-[50%] -translate-x-[50%] mt-5 flex flex-row gap-5">
        <div
          className=" w-[250px] rounded-full h-6 bg-gray-700 flex justify-center items-center cursor-pointer"
          onClick={prevSlide}
        ></div>
        <div
          className=" w-[250px] rounded-full h-6 bg-white flex justify-center items-center cursor-pointer"
          onClick={nextSlide}
        ></div>
      </div>
    </div>
  );
};

export default NewFadeSlider;
