import React, { useState, useEffect, useRef } from "react";

const fadeslider = () => {
  const [scrollCor, setScrollCor] = useState(0);

  const scrollHandler = (e) => {
    let y = window.scrollY;
    setScrollCor(y);
    console.warn(scrollCor);
  };

  const addAnimaton = () => {
    let height = screen.height;
    let slide1 = document.getElementById("slide1");
    let slide2 = document.getElementById("slide2");
    let slide3 = document.getElementById("slide3");

    if (scrollCor > height && scrollCor < 2 * height) {
      slide1.style.opacity = 1;
      slide1.style.transform = "translate(0px,0px)";
      slide2.style.opacity = 0;
      slide2.style.transform = "translate(0px,250px)";
      slide3.style.opacity = 0;
      slide3.style.transform = "translate(0px,2500px)";
    } else if (scrollCor >= 2 * height && scrollCor < 3 * height) {
      slide1.style.opacity = 0;
      slide2.style.opacity = 1;
      slide3.style.opacity = 0;
    } else if (scrollCor >= 3 * height) {
      slide1.style.opacity = 0;
      slide2.style.opacity = 0;
      slide3.style.opacity = 1;
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", scrollHandler);
    console.warn(scrollCor);
    addAnimaton();
  });
  return (
    <div className=" relative h-[500vh]">
      <div className=" sticky top-0 left-0 h-screen w-full flex items-center bg-red-500">
        <div
          className=" absolute top-0 left-0 h-full w-1/2 p-16 opacity-1 translate-y-[250px] bg-green-500"
          id="slide1"
        >
          {" "}
          uhybcjnxewicx
        </div>
        <div
          className=" absolute top-0 left-0 h-full w-1/2 p-16 opacity-0 translate-y-[250px] bg-pink-500"
          id="slide2"
        >
          {" "}
          hjbcnjdxknms
        </div>
        <div
          className=" absolute top-0 left-0 h-full w-1/2 p-16 opacity-0 translate-y-[250px] bg-blue-500"
          id="slide3"
        >
          {" "}
          njhxnwjkxkxm
        </div>
      </div>
    </div>
  );
};

export default fadeslider;
