import React from "react";
import FadeSlider from "./FadeSlider";
import NewFadeSlider from "./NewFadeSlider";

const Page = () => {
  const [screenWidth, setScreenWidth] = React.useState(window.innerWidth);
  React.useEffect(() => {
    window.addEventListener("resize", () => {
      setScreenWidth(window.innerHeight);
    });
  });
  return (
    <div className=" flex lg:flex-row flex-col">
      <div className=" bg-black w-full lg:w-1/2 lg:h-[600vh] text-white">
        <div className=" w-[75%] h-screen py-5 mx-auto lg:sticky lg:top-[50%] lg:-translate-y-[50%] lg:left-0">
          <h1 className=" text-4xl lg:text-6xl lg:mx-10 font-extrabold">
            Create at speed of thought
          </h1>
          <br />
          <p className=" hidden lg:block my-5 px-11">
            focus on your getting your thoughts out and creating the best
            message while Chronicle does the heavylifting for you
          </p>
        </div>
      </div>
      {screenWidth > 1024 ? <FadeSlider /> : <NewFadeSlider />}
    </div>
  );
};

export default Page;
